import React, { Component } from 'react';
import './App.css';
import Tabela from './Tabela'
import Form from './Formulario'

class App extends Component {

  state = {
    autores:  [
      {
        nome: 'Paulo',
        livro: 'React',
        preco: '1000'
      },
      {
        nome: 'Daniel',
        livro: 'Java',
        preco: '99'
      },
      {
        nome: 'Marcos',
        livro: 'Design',
        preco: '150'
      },
      {
        nome: 'Bruno',
        livro: 'DevOps',
        preco: '100'
      },
      {
        nome: 'Alexadre',
        livro: 'Java',
        preco: '250'
      }
    ]
  };

  removeAutor = (index) => {

    const { autores } = this.state;

    this.setState(
      {
        autores : autores.filter((autor, posAtual) => {
          return posAtual !== index;
        })
      }
    );
  }

  render() {
    return (
      <div className="App">
        <Tabela autores = { this.state.autores } removeAutor = { this.removeAutor} />

      </div>
    );
  }

}

export default App;
